/* Save as styles.css */
body {
    background-color: #f0f0f0;
    font-family: Arial, sans-serif;
    text-align: center;
    padding: 50px;
}

h1 {
    color: #2E75B6;
    font-size: 48px;
}

p {
    color: #333;
    font-size: 20px;
}

// Save as script.js
console.log("Hello, World!");

let name = "Student";
console.log("Welcome, " + name + "!");

// Alert in browser
alert("Hello from JavaScript!");

// Change HTML content
document.querySelector('h1').textContent = "JavaScript is working!";
